import React, { Component } from 'react'

export default class Generate extends Component {
    constructor(props) {
        super(props)
        this.state = {
            title: '',
            desc: ''
        }
    }

    share = () => {
        alert(`${this.state.title}\n${this.state.desc}`);
    }

    render() {
        return (
            <div>
                <div className="userInput">
                    <label>Title:</label>

                    <input type="text" onChange={
                        event => this.setState({
                            title: event.value                    })
                    }></input>

                    <label>Description:</label>

                    <textarea onChange={
                        event => this.setState({
                            desc: event.value
                        })
                    }></textarea>

                    <button onClick={this.share}>Generate</button>
                </div>
            </div>
        )
    }
}
