import React, { Component } from 'react'
import Post from './Post'

export default class Generate extends Component {
    constructor(props) {
        super(props)
        this.state = {
            title: '',
            desc: '',
            key: 0
        }
    }

    handleTitleChange = (event) => {
        this.setState({
            title: event.target.value
        })
    }

    handleDescChange = (event) => {
        this.setState({
            desc: event.target.value
        })
    }

    incrementKey = () => {
        this.setState({
            key: this.state.key++
        })
    }

    sendData = () => {
        this.props.callBack(<Post title={this.state.title} desc={this.state.desc} key={this.state.key}/>);
        this.incrementKey
    }

    render() {
        return (
            <div>
                <div className="userInput">
                    <label>Title:</label>

                    <input type="text" value={this.state.title}
                    onChange={this.handleTitleChange}></input>

                    <label>Description:</label>

                    <textarea value={this.state.desc}
                    onChange={this.handleDescChange}></textarea>

                    <button onClick={this.sendData}>Generate</button>
                </div>
            </div>
        )
    }
}
